  def merge_the_tools(string, k):
  sub_str = []
  for i in range(len(string)):
    if string[i] not in sub_str:
      sub_str.extend(string[i])
    if (i+1) % k == 0:
      print(''.join(sub_str))
      del sub_str[:]

