  {% code lang:python %}
  # The planets sorted in alphabetical order
  sorted(planets) {% endcode %}

  {% code %}
  ['Earth', 'Jupiter', 'Mars', 'Mercury', 'Neptune', 'Saturn', 'Uranus', 'Venus'] {% endcode %}
