x = input()

if(ord(x)>96 and ord(x)<123):   # 소문자
    print("{0}(ASCII: {1}) => {2}(ASCII: {3})".format(x, ord(x), chr(ord(x)-32), ord(x)-32))
elif(ord(x)>64 and ord(x)<91):  # 대문자
    print("{}(ASCII: {}) => {}(ASCII: {})".format(x, ord(x), x.lower(), ord(x.lower())))
else:
    print(x)
