    def name(self):
        return self._name

    @name.setter
    def name(self, name):
        self._name = name

    def show(self):
        print("이름: "+self._name1)

class GraduateStudent(Student):
    """Child/Sub class"""
    __major = "컴퓨터"

    def __init__(self):
        self.__name = super()._name2

