def pizza():
    # 초기 화덕 큐
    oven = []
    # 피자 번호
    pNum = 0

    # (init) 빈 화덕에 피자 채우기
    for _ in range(N):
        oven.append([C[pNum], pNum])
        pNum += 1

    # (반복) 오븐에 마지막 피자만 남을 때 까지
    while len(oven) != 1:
        # 현재 화덕 확인으로 치즈가 반 녹음
        oven[0][0] = oven[0][0] // 2

        # 치즈가 다 녹았다면
        if oven[0][0] == 0:
            # 꺼내고
            oven.pop(0)
            # 피자가 남았다면
            if pNum != len(C):
                # 화덕에 넣어준다
                oven.append([C[pNum], pNum])
                pNum += 1
        # 덜 녹았으면 회전
        else:
            oven.append(oven.pop(0))

    return oven[0][1] + 1


T = int(input())

