T = int(input())

for test_case in range(1, T+1):
    # 일단 입력 받고
    seq_string = list(input())
    # 스택을 만들어서 한 단어씩 push, pop 한다
    stack = []

    for word in seq_string:
        # 맨 처음 스택이 비어있을 때,
        if len(stack) == 0:
            stack.append(word)
        # 그 외, 새로 입력되는 문자와 스택 마지막 문자가 같으면 pop
        else:
            if stack[-1] == word:
                stack.pop()
            else:
                stack.append(word)

    print('#{} {}'.format(test_case, len(stack)))
