  {% code lang:python %}
  if __name__ == '__main__':
    set_A = {n for n in input().split()}
    ans = True

    for _ in range(int(input())):
      other = {n for n in input().split()}
      if not other.issubset(set_A):
        ans = False

  print(ans)  {% endcode %}

