  <!-- endtab -->
{% endtabs %}

{% note default no-icon %}
  {% code lang:python %}
  a = int(input())
  b = int(input())

  print(a//b)
  print(a%b)
  print(divmod(a, b))  {% endcode %}
{% endnote %}

{% note success no-icon %}
