{% note success no-icon %}
  Counter 모듈을 사용한 구현
  {% code lang:python %}
  from collections import Counter

  X = int(input())
  shoes = [int(size) for size in input().split()]
  N = int(input())
  shoes_counter = Counter(shoes)
  earned = 0

  for _ in range(N):
      size, price = map(int, input().split())
      if shoes_counter[size] > 0:
          earned += price
          shoes_counter[size] -= 1

