  1252478965
  <!-- endtab -->

  <!-- tab OUTPUT @code -->
  YES
  NO
  <!-- endtab -->
{% endtabs %}

{% note success no-icon %}
  {% code lang:python %}
  import re

  for _ in range(int(input())):
      line = input()
      if re.match(r"^[789]{1}\d{9}$", line):
          print("YES")
      else:
          print("NO") {% endcode %}
