def summation_of_primes_with_eratosthenes(n):
    sieve = [True] * (n + 1)
    sieve[0] = sieve[1] = False
    total = 0

    for prime, isPrime in enumerate(sieve):
        if isPrime:
            total += prime
            for idx in range(prime*prime, n + 1, prime):
                sieve[idx] = False

    return total


