  38
  <!-- endtab -->
{% endtabs %}

{% note default no-icon %}
  `eval()`   
  {% code lang:python %}
  if __name__ == '__main__':
    A = int(input())
    set_A = set(input().split())
    N = int(input())

    for _ in range(N):
        operation, n = input().split()
        set_n = input().split()
        eval('set_A.{}({})'.format(operation, set_n))

    print(sum([int(i) for i in set_A])){% endcode %}
{% endnote %}

