from math import sqrt


def largest_prime_factor(N):
  prime = []

  while N % 2 == 0:
      prime.append(2)
      N //= 2
  for i in range(3, int(sqrt(N))+1, 2):
      while N % i == 0:
          prime.append(i)
          N //= i

  if N > 2:
      prime.append(N)

  return max(prime)


if __name__ == '__main__':
