    #  '//'     
    return {'+':n1+n2, '-':n1-n2, '*':n1*n2, '/':n1//n2}[op]

def calc_code(code):
    stack = []

    for token in code:
        if token == '.':
            if len(stack) == 1:
                return stack.pop()
            #    stack   
            else:
                return 'error'

        if token.isdigit():
            stack.append(token)
        else:
            try:
                num2, num1 = int(stack.pop()), int(stack.pop())
                stack.append(operator(token, num1, num2))
            # stack       
            except IndexError:
                return 'error'


